import React, { Component } from "react";
import Child from "./../Child/Child";

export default class Parent extends Component {
  state = {
    userName: "mahmoud",
    lastName: "kadry",
    job: "idk",
    age: "26",
    productList: [
      {
        pName: "xaiomi",
        pPrice: 3000,
        pCat: "mobile",
        cound: 22,
        pKey: 1,
        count: 10,
      },
      {
        pName: "apple",
        pPrice: 35000,
        pCat: "mobile",
        cound: 5,
        pKey: 2,
        count: 12,
      },
      {
        pName: "samsung",
        pPrice: 45000,
        pCat: "mobile",
        cound: 219,
        pKey: 3,
        count: 9,
      },
      {
        pName: "xyz",
        pPrice: 45000,
        pCat: "mobile",
        cound: 219,
        pKey: 5,
        count: 399,
      },
      {
        pName: "samsudsdng",
        pPrice: 45000,
        pCat: "mobile",
        cound: 219,
        pKey: 6,
        count: 110,
      },
      {
        pName: "samsungsdasd",
        pPrice: 45000,
        pCat: "mobile",
        cound: 219,
        pKey: 69,
        count: 120,
      },
    ],
  };

  productDelete = (index) => {
    let newProductList = [...this.state.productList];
    let list = newProductList.splice(index, 1);
    this.setState({ productList: newProductList });
  };
  productUpdate = (index) => {
    let arr = [...this.state.productList];
    arr[index].count++;
    this.setState({ productList: arr });
  };
  componentDidMount() {
    console.log("mount");
  }
  componentDidUpdate() {
    console.log("did upadate");
    setInterval(() => {
      console.log("upadte");
    }, 1000);
  }
  componentWillUnmount() {
    clearInterval
  }

  render() {
    return (
      <>
        <div className="container">
          <div className="row gy-2 gap-1 justify-content-between">
            {this.state.productList.map((product, index) => {
              return (
                <Child
                  product={product}
                  key={product.pKey}
                  productDelete={this.productDelete}
                  index={index}
                  update={this.productUpdate}
                />
              );
            })}
          </div>
        </div>
      </>
    );
  }
}
